package com.restwiz.keyvaluedao.controller;

import com.restwiz.keyvaluedao.KeyValueDao;
import java.lang.String;
import org.springframework.web.bind.annotation.*;
import org.springframework.http.MediaType;
import org.springframework.beans.factory.annotation.Autowired;
import com.wordnik.swagger.annotations.Api;
import com.wordnik.swagger.annotations.ApiOperation;
import com.wordnik.swagger.annotations.ApiParam;
import com.wavemaker.tools.api.core.annotations.WMAccessVisibility;
import com.wavemaker.tools.api.core.models.AccessSpecifier;

@RestController
@RequestMapping(value = "/keyValueDao")
public class KeyValueDaoController {

    @Autowired
    private KeyValueDao keyValueDao;

    @RequestMapping(value = "/key", produces = "application/json", method = RequestMethod.GET)
    public String getKey() {
        return keyValueDao.getKey();
    }

    @RequestMapping(value = "/value", produces = "application/json", method = RequestMethod.GET)
    public String getValue() {
        return keyValueDao.getValue();
    }

    @RequestMapping(value = "/key", method = RequestMethod.PUT)
    public void setKey(@RequestParam(value = "key", required = false) String key) {
        keyValueDao.setKey(key);
    }

    @RequestMapping(value = "/value", method = RequestMethod.PUT)
    public void setValue(@RequestParam(value = "value", required = false) String value) {
        keyValueDao.setValue(value);
    }
}
